generated.css:
html {
  line-height: 1;
}

body {
  align-items: center;
  background-color: #111;
  display: flex;

  font-family: Helvetica Neue, sans-serif;

  height: 100vh;
  justify-content: center;

  margin: 0;
  padding: 0;

  text-align: center;
}

h1 {
  color: #00caff;

  font-weight: 100;
  font-size: 8em;

  margin: 0;
  padding-bottom: 15px;
}

button {
  background: #111;
  border-radius: 0px;
  border: 1px solid #00caff;
  color: #00caff;

  font-size: 2em;
  font-weight: 100;

  margin: 0;

  outline: none;
  padding: 5px 15px;

  transition: background .2s;     

  &:hover, &:active, &:disabled {
    background: #00caff;
    color: #111; 
  }
  &:active {
    outline: 2px solid #00caff;
  }
  &:focus {
    border: 1px solid #00caff;
  }
}

button + button {
  margin-left: 3px;
}
